import{_ as s,o as a,c as e,U as n}from"./chunks/framework.835c9fd2.js";const y=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"Nginx/正向代理和反向代理.md","filePath":"Nginx/正向代理和反向代理.md","lastUpdated":1632912264000}'),l={name:"Nginx/正向代理和反向代理.md"},o=n(`<h3 id="正向代理、反向代理" tabindex="-1">正向代理、反向代理 <a class="header-anchor" href="#正向代理、反向代理" aria-label="Permalink to &quot;正向代理、反向代理&quot;">​</a></h3><p>正向代理服务器是用来让局域网客户机接入外网以访问外网资源；</p><p>反向代理服务器是用来让外网的客户端接入局域网中的站点以访问站点中的资源；</p><h4 id="正向代理" tabindex="-1">正向代理 <a class="header-anchor" href="#正向代理" aria-label="Permalink to &quot;正向代理&quot;">​</a></h4><p>一般在搭建 Nginx 服务器中单独配置一个 server 块用来设置代理服务；</p><ul><li><p><code>resolver</code>： 该指令用于指定 DNS 服务器的 IP 地址。DNS 服务器的主要工作是进行域名解析，将域名映射为对应的 IP 地址；</p><div class="language-nginx"><button title="Copy Code" class="copy"></button><span class="lang">nginx</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#89DDFF;">resolver </span><span style="color:#A6ACCD;">address ... [vaild=time]</span><span style="color:#89DDFF;">;</span></span></code></pre></div><ul><li><code>address</code>：DNS 服务器的 IP 地址，默认使用53端口；</li><li><code>time</code>：设置数据包在网络中的有效时间；</li></ul></li><li><p><code>resolver_timeout</code>：用于设置 DNS 服务器域名解析超时时间；</p><div class="language-nginx"><button title="Copy Code" class="copy"></button><span class="lang">nginx</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#89DDFF;">resolver_timeout </span><span style="color:#A6ACCD;">time</span><span style="color:#89DDFF;">;</span></span></code></pre></div></li><li><p><code>proxy_pass</code>：用于设置代理服务器的协议和地址；（反向代理）</p><div class="language-NGINX"><button title="Copy Code" class="copy"></button><span class="lang">NGINX</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#89DDFF;">proxy_pass </span><span style="color:#A6ACCD;">URL</span><span style="color:#89DDFF;">;</span></span></code></pre></div><p>URL 即为设置的代理服务器协议和地址；</p></li></ul><h4 id="反向代理" tabindex="-1">反向代理 <a class="header-anchor" href="#反向代理" aria-label="Permalink to &quot;反向代理&quot;">​</a></h4><ul><li><p><code>proxy_pass</code></p><div class="language-nginx"><button title="Copy Code" class="copy"></button><span class="lang">nginx</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#89DDFF;">proxy_pass </span><span style="color:#A6ACCD;">URL</span><span style="color:#89DDFF;">;</span></span></code></pre></div><p>eg:</p><div class="language-nginx"><button title="Copy Code" class="copy"></button><span class="lang">nginx</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#89DDFF;">proxy_pass </span><span style="color:#A6ACCD;">http://www.myweb.name/uri</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#89DDFF;">proxy_pass </span><span style="color:#A6ACCD;">http://localhost:8080/uri/</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#89DDFF;">proxy_pass </span><span style="color:#A6ACCD;">http://unix:/temp/backend.socket:/uri/</span><span style="color:#89DDFF;">;</span></span></code></pre></div></li></ul>`,8),p=[o];function t(c,i,r,d,_,D){return a(),e("div",null,p)}const u=s(l,[["render",t]]);export{y as __pageData,u as default};
